function greenajax(e,r){if(e.method=e.method.toUpperCase(),"GET"===e.method||"DELETE"===e.method||"HEAD"===e.method){if(!e.method||!e.url)throw new Error("the values of the url and method options are required")}else if(!e.method||!e.url||!e.body)throw new Error("the values of the url, method and body options are required");fetch(e.url,{method:e.method,body:JSON.stringify(e.body),mode:e.mode||"cors",credentials:e.credentials||"same-origin",catch:e.catch||"default",redirect:e.redirect||"follow",referrer:e.referrer||"about:client",referrerPolicy:e.referrerPolicy||"no-referrer",headers:e.headers||{"Content-Type":"application/json"}}).then(e=>e.json()).then(r).catch(e=>{throw e})}